  -moz-backface-visibility: hidden;
  -ms-backface-visibility: hidden;
  -o-backface-visibility: hidden;
  -webkit-transform: translate3d(0, 0, 0);
  -khtml-transform: translate3d(0, 0, 0);
  -moz-transform: translate3d(0, 0, 0);
  -ms-transform: translate3d(0, 0, 0);
  -o-transform: translate3d(0, 0, 0);
  transform: translate3d(0, 0, 0);
}

.hero__backgorund {
  position: absolute;
  display: block;
  top: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
  background: #666;
}

.hero__backgorund .item {
  position: absolute;
  display: block;
  width: 100%;
  height: 100%;
}

.hero__backgorund.owl-carousel .owl-item {
  width: 100%;
  min-height: 100vh !important;
}

.owl-carousel {
  position: relative;
  height: 100%;
}

.main__container {
  position: fixed;
  display: table;
  table-layout: fixed;
  top: 0;
  width: 100%;
  height: 100%;
}

.contain__wrapper {
  position: relative;
  display: table-cell;
  vertical-align: middle;
}

.music__wrapper {
  position: relative;
  width: 700px;
  height: 350px;
  background: #19181E;
  margin: 0 auto;
  box-shadow: 0 5px 30px rgba(0, 0, 0, 0.8);
  overflow: hidden;
}

.left__panel {
  position: absolute;
  left: 0;
  width: 350px;
  height: 350px;
  background: #4729A2;
  cursor: w-resize;
  transform: translateX(0);
  transition: all 500ms cubic-bezier(0.65, 0.19, 0.1, 0.93) 0ms;
}

.right__panel {
  position: absolute;
  right: 0;
  width: 350px;
  height: 350px;
  background: #19181E;
  transform: translateX(0);
  transition: all 500ms cubic-bezier(0.65, 0.19, 0.1, 0.93) 0ms;
}

.header {
  position: relative;
  width: 350px;
  height: 120px;
  background: #09090B;
}

.icon-right {
  display: block;
  text-align: right;
  font-size: 24px;
  color: #ffffff;
  padding: 10px 20px;
  cursor: pointer;
}

.header .song__name {
  color: #CDC2FF;
  font-size: 1.45em;
  font-weight: 300;
  letter-spacing: 1px;
  text-transform: uppercase;
  text-align: center;
}

.header .album__name {
  color: #ffffff;
  font-size: 1em;
  font-weight: 500;
  letter-spacing: 1px;
  text-transform: uppercase;
  text-align: center;
}

.music__control {
  position: relative;
  width: 350px;
  height: 230px;
}

.music__button {
  position: absolute;
  bottom: 0;
  width: 100%;
  /* margin: auto; */
  text-align: center;
}

.button__pause {
  display: inline-block;
  height: 70px;
  width: 70px;
  background: #2D3446;
  border-radius: 50%;
  box-shadow: 0 0 1px rgb(45, 52, 70);
  font-size: 46px;
  margin: 0 10px;
  text-align: center;
  line-height: 70px;
  color: #FFF;
  cursor: pointer;
}

.button__prev,
.button__next {
  display: inline-block;
  height: 50px;
  width: 50px;
  background: #7C60FF;
  border-radius: 50%;
  box-shadow: 0 0 1px rgb(193, 193, 193);
  font-size: 32px;
  text-align: center;
  line-height: 50px;
  color: #FFF;
  vertical-align: bottom;
  cursor: pointer;
  transition: all 300ms ease;
}

.button__pause:active,
.button__prev:active,
.button__next:active {
  background: #4729A2;
  transition: all 300ms ease;
}

input[type=range] {
  background-color: silver;
  width: 300px;
  height: 5px;
  border-radius: 3px;
  margin: 30px 0;
}

input[type=range]::-webkit-slider-thumb {
  background-color: #7C60FF;
  width: 20px;
  height: 20px;
  border-radius: 10px;
}

.display {
  display: none;
}

/* Feel free to change duration  */
.animated {
  animation-duration: 100ms;
  animation-fill-mode: both;
}

.owl-animated-out {
  z-index: 1;
}

.owl-animated-in {
  z-index: 0;
}

@-webkit-keyframes slideInDown {
  0% {
    opacity: 0;
    transform: translateY(-300px);
  }
  100% {
    transform: translateY(0);
  }
}
@keyframes slideInDown {
  0% {
    opacity: 0;
    transform: translateY(-300px);
  }
  100% {
    -webkit-transform: translateY(0);
    -ms-transform: translateY(0);
    transform: translateY(0);
  }
}
.slideInDown {
  -webkit-animation-name: slideInDown;
  animation-name: slideInDown;
}

@-webkit-keyframes slideOutDown {
  0% {
    -webkit-transform: translateY(0);
    transform: translateY(0);
  }
  100% {
    opacity: 0;
    -webkit-transform: translateY(300px);
    transform: translateY(300px);
  }
}
@keyframes slideOutDown {
  0% {
    -webkit-transform: translateY(0);
    -ms-transform: translateY(0);
    transform: translateY(0);
  }
  100% {
    opacity: 0;
    -webkit-transform: translateY(300px);
    -ms-transform: translateY(300px);
    transform: translateY(300px);
  }
}
.slideOutDown {
  -webkit-animation-name: slideOutDown;
  animation-name: slideOutDown;
}

